import React, { useState } from 'react';

const Folder = ({ folder, onDelete, onRename }) => {
  const [subfolders, setSubfolders] = useState([]);

 /* const addSubfolder = () => {
    const newSubfolder = {
      id: subfolders.length + 1,
      name: `Subfolder ${subfolders.length + 1}`,
      subfolders: [],
    };
    setSubfolders([...subfolders, newSubfolder]);
  };*/

  return (
    <div style={{ marginLeft: 20 }}>
      <div>
        <input
          type="text"
          value={folder.name}
          onChange={(e) => onRename(folder.id, e.target.value)}
        />
        <button onClick={() => onDelete(folder.id)}>Delete</button>
      </div>
      <button onClick={addSubfolder}>Add Subfolder</button>
      {subfolders.map((subfolder) => (
        <Folder
          key={subfolder.id}
          folder={subfolder}
          onAddSubfolder={addSubfolder}
          onDelete={onDelete}
          onRename={onRename}
        />
      ))}
    </div>
  );
};

const TreeView = () => {
  const [folders, setFolders] = useState([
  ]);

  const deleteFolder = (id) => {
    const updatedFolders = folders.filter((folder) => folder.id !== id);
    setFolders(updatedFolders);
  };

  const renameFolder = (id, newName) => {
    const updatedFolders = folders.map((folder) =>
      folder.id === id ? { ...folder, name: newName } : folder
    );
    setFolders(updatedFolders);
  };

  const addSubfolder = (parentId) => {
    const updatedFolders = folders.map((folder) => {
      if (folder.id === parentId) {
        const newSubfolder = {
          id: folder.subfolders.length + 1,
          name: `Subfolder ${folder.subfolders.length + 1}`,
          subfolders: [],
        };
        return {
          ...folder,
          subfolders: [...folder.subfolders, newSubfolder],
        };
      }
      return folder;
    });
    setFolders(updatedFolders);
  };

  return (
    <div>
      <button onClick={() => setFolders([...folders, { id: folders.length + 1, name: `Folder ${folders.length + 1}`, subfolders: [] }])}>
        Add Folder
      </button>
      {folders.map((folder) => (
        <Folder
          key={folder.id}
          folder={folder}
          onAddSubfolder={() => addSubfolder(folder.id)}
          onDelete={deleteFolder}
          onRename={renameFolder}
        />
      ))}
    </div>
  );
};

export default TreeView;
