import React, { useState } from 'react';

const TreeComponent = () => {
  const [tree, setTree] = useState([]);

  const addFolder = (parentId) => {
    const newFolder = {
        id: `Folder ${parentId}.${tree.length + 1}`,
        name: `Folder ${parentId}.${tree.length + 1}`
    };
    //setTree([...tree, newFolder]);
    const updatedTree = tree.map(node => {
        if (node.id === parentId) {
          return {
            ...node,
            children: [...(node.children || []), newFolder]
          };
        }
        return node;
      });
  
      setTree(updatedTree);
  };

  

  return (
    <div>
      <button onClick={() => addFolder('root')}>Добавить папку</button>
      {tree.map(folder => (
        <div key={folder.id}>
          <span>{folder.name}</span>
          <button onClick={() => addFolder(folder.id)}>Добавить подпапку</button>
          {folder.children && folder.children.map(child => (
            <div key={child.id}>
              <span>{child.name}</span>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default TreeComponent;
