import React, { useState } from 'react';

const TreeComponent = () => {
  const [tree, setTree] = useState([]);

  return (
    <div>
      <button onClick={handleAddRootElement}>Добавить1</button>
      {tree.map(node => (
        <div key={node.id}>
          <span>{node.name}</span>
          <button onClick={() => setTree(handleAddElement(node.id, tree))}>Добавить2 {node.name}</button>
          {node.children.map(child => (
            <div key={child.id}>
              <span>{child.name}</span>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default TreeComponent;
