import React, { useState } from 'react';

const TreeComponent = () => {
  const [tree, setTree] = useState([]);

  const addFolder = () => {
    const newFolder = {
      id: `Folder ${tree.length + 1}`,
      name: `Folder ${tree.length + 1}`
    };
    setTree([...tree, newFolder]);
  };

  return (
    <div>
      <button onClick={addFolder}>Добавить папку</button>
      {tree.map(folder => (
        <div>
            <div key={folder.id}>
            <span>{folder.name}</span>
            </div>
            <button onClick={addFolder}>Добавить папку</button>
        </div>
      ))}
    </div>
  );
};

export default TreeComponent;
