import React, { useState } from 'react';

const Folder = ({ folder, onDelete, onRename }) => {
  const [subfolders, setSubfolders] = useState([]);

  const addSubfolder = () => {
    const newSubfolder = {
      id: subfolders.length + 1,
      name: `Subfolder ${subfolders.length + 1}`,
      subfolders: [],
    };
    setSubfolders([...subfolders, newSubfolder]);
  };

  return (
    <div style={{ marginLeft: 20 }}>
      <div>
        <input
          type="text"
          value={folder.name}
          onChange={(e) => onRename(folder.id, e.target.value)}
        />
        <button onClick={() => onDelete(folder.id)}>Delete</button>
      </div>
      <button onClick={addSubfolder}>Add Subfolder</button>
      {subfolders.map((subfolder) => (
        <Folder
          key={subfolder.id}
          folder={subfolder}
          onAddSubfolder={addSubfolder}
          onDelete={onDelete}
          onRename={onRename}
        />
      ))}
    </div>
  );
};

export default TreeView;
